import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  name: string = 'william. (use your own name)';
}
<div class="px-5">
  <input type="text" [(ngModel)]="name">
</div>

<h1 class="display-1"> ↑↑ {{ name }}</h1>
<p class="blockquote">
  Lorem ipsum dolor sit amet consectetur adipisicing elit. Sapiente accusamus
  itaque quis maiores consequuntur, assumenda, mollitia doloremque delectus,
  ipsa unde tempora saepe doloribus? Tempore quasi dolores temporibus excepturi,
  fuga sequi.
</p>
<footer class="blockquote-footer">someone famous who had free time to write gibberish</footer>
