import { Component } from "@angular/core";

@Component({
  selector: "app-article",
  templateUrl: "./article.component.html",
  styleUrls: ['./article.component.css']

})

export class ArticleComponent{
  title:string = 'Whatever you want'
  content:string = "Some content goes here. How about Lorem ipsum dolor sit, amet consectetur adipisicing elit. Fugiat, assumenda. Quae, eveniet sint ullam enim explicabo sapiente alias voluptas doloremque delectus nam reprehenderit. Illum ipsum necessitatibus corporis ad nemo est. "
}
<div class="card mt-3 box">
  <p class="text-center">Stuff from the article.html page</p>
  <hr>
  <p class="text-center"> The instructions say to put "{{ title }}" here</p>
  <hr>
  <p> {{ content }} </p>
</div>
