import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  name = 'Joseph said your name goes in here';

  quote = '';

  boxes = [0, 0, 0, 0, 0];
}
<h1 class="bg-success">Howdy peeps {{ name }}</h1>
<app-box *ngFor="let box of boxes"></app-box>
<!-- <app-box></app-box>
<app-box></app-box> -->
<input type="text" [(ngModel)]="quote" />
<div class="border">{{ quote }}</div>
